userDefinedLinkHintCss:
/* Oxocarbon */

:root {
  --text: #ffffff;
  --green: #42be65;
  --magenta: #be95ff;
  --pink: #ff7eb6
  --blue: #33b1ff;
  --cyan: #82cfff;
  --grey: #393939;
  --base: #161616;
  --mantle: #262626;
}

#vimiumHintMarkerContainer div.internalVimiumHintMarker, #vimiumHintMarkerContainer div.vimiumHintMarker {
  padding: 3px 4px;
  background: var(--base);
  border: 0;
}

#vimiumHintMarkerContainer div span {
  color: var(--magenta);
  text-shadow: none;
}

#vimiumHintMarkerContainer div > .matchingCharacter ~ span {
  color: var(--text);
}

#vomnibar {
  background: var(--base);
  animation: show 200ms cubic-bezier(0, 0, 0.2, 1) forwards;
  box-shadow: none;
  border: 0;
}

@keyframes show {
  0% {
    transform: translateY(50px);
    opacity: 0;
  }
  100% {
    transform: translateY(0);
    opacity: 1;
  }
}

#vomnibar input {
  color: var(--text);
  background: var(--blue);
  border: none;
  height: unset;
  padding: 16px 30px;
  font-family: inherit;
}

#vomnibar .vomnibarSearchArea {
  border: none;
  padding: unset;
  background: var(--base)
}

#vomnibar ul {
  padding: 0;
  margin: 0;
  background: var(--base);
  border-top: 1px solid var(--grey);
}

#vomnibar li {
  padding: 15px;
  border-bottom: 1px solid var(--grey)
  font-size: 16px;
}

#vomnibar li .vomnibarTopHalf,
#vomnibar li .vomnibarBottomHalf {
  padding: 3px 0;
}

#vomnibar li .vomnibarSource {
  color: var(--cyan);
}

#vomnibar li em,
#vomnibar li .vomnibarTitle {
  color: var(--magenta);
  font-weight: bold;
}

#vomnibar li .vomnibarUrl {
  color: var(--text);
}

#vomnibar li .vomnibarMatch {
  color: var(--green);
  font-weight: bold;
}

#vomnibar li .vomnibarTitle .vomnibarMatch {
  color: var(--magenta);
}

#vomnibar li.vomnibarSelected {
  background-color: var(--mantle);
}

#vomnibarInput::selection {
  background-color: var(--grey);
}

#vomnibar li .vomnibarIcon {
  vertical-align: inherit;
}

.vimiumReset,
div.vimiumReset,
span.vimiumReset,
table.vimiumReset,
a.vimiumReset,
a:visited.vimiumReset,
a:link.vimiumReset,
a:hover.vimiumReset,
td.vimiumReset,
tr.vimiumReset {
    font-family: inherit;
}
